mapper.select:
SELECT ,
       sr.*
FROM sys_user
         JOIN sys_role_user sru ON sys_user.id = sru.user_id
         JOIN sys_role sr ON sru.role_id = sr.id


        and sr.id = #{role.id}


        and sys_user.name like concat('%',#{name},'%')


        and sys_user.status = #{status}


        and sys_user.number = #{status}


        and sys_user.mobile = #{mobile}


        and sys_user.email = #{email}


        and (sys_user.address like concat('%',#{address},'%')
             or sys_user.country like concat('%',#{address},'%')
             or sys_user.province like concat('%',#{address},'%')
             or sys_user.city like concat('%',#{address},'%')
             or sys_user.district like concat('%',#{address},'%')
             )
